package bucles;

public class EjemploWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//BUCLES --> while , for , do while, for para recorrer matrices
	
		//variable de control del bucle --> i,j,k ...
		//condicion de continuidad.
		//incicialización, condición , modificar la variable de control
		
		//Programa que muestra por pantalla los 20 primeros números
		int i ;
		//inicializar a 1
		i = 100;
		
		while(i<=20) {
			System.out.print(i + " ");
			i++;
		}
		System.out.println();
		System.out.println("Fin del bucle");
		
	}

}
